import React, {useState} from 'react';
import {useHistory} from 'react-router-dom'
import "../Choose.css"

const ChooseTest = () => {
    const history = useHistory()
    const [test, setTest] = useState('');

    var Version = '';

    const handleChange = ()=>{
        Version = ''
        setTest(event.target.value);
        

    }
    const ChangeColor = (black, normal) =>{
        if(['IAA', 'IAB'].includes(black)){document.getElementById(black).style.backgroundColor ='#53BF9D';}
        else if(['IGA', 'IGB'].includes(black)){document.getElementById(black).style.backgroundColor ='#BD4291'}
        else if(['TA', 'TB'].includes(black)){document.getElementById(black).style.backgroundColor ='#F94C66'}
        
        document.getElementById(normal).style.backgroundColor ='';
    }

    const IELTSAcademicVersions = () =>{
        return(
        <>
        <div className='version' id='IAA'><label><input onChange={()=>{Version = "./IELTSAcademicA"; ChangeColor('IAA','IAB')}} style={{visibility:'collapse'}} type="radio" name="version" /> Academic IELTS A</label></div>
        <div className='version' id='IAB'><label><input onChange={()=>{Version = "./IELTSAcademicB"; ChangeColor('IAB','IAA')}} style={{visibility:'collapse'}} type="radio" name="version" /> Academic IELTS B</label></div>
        </>)    
    }
    const IELTSGeneralVersions = () =>{
        return (
        <>
        <div className='version' id='IGA'><label><input onChange={()=>{Version = "./IELTSGeneralA"; ChangeColor('IGA','IGB')}} style={{visibility:'collapse'}} type="radio" name="version"/> General IELTS A</label></div>
        <div className='version' id='IGB'><label><input onChange={()=>{Version = "./IELTSGeneralB"; ChangeColor('IGB','IGA')}} style={{visibility:'collapse'}} type="radio" name="version"/> General IELTS B</label></div>
        </>)
    }
    const TOELFVersions = () =>{
        return (<>
        <div className='version' id='TA'><label><input onChange={()=>{Version = "./TOELFA"; ChangeColor('TA','TB')}} style={{visibility:'collapse'}} type="radio" name="version"/> TOELF A</label></div>
        <div className='version' id='TB'><label><input onChange={()=>{Version = "./TOELFB"; ChangeColor('TB','TA')}} style={{visibility:'collapse'}} type="radio" name="version"/> TOELF B</label></div>
        </>)
    }
    return(
        <div>
            <div className='background'></div>
            <div className="testContainer">
                <div className='test' id='acielts'><label><input onChange={handleChange} type="radio" style={{visibility:'collapse'}} value="IELTSAcademic" name="test"/>Academic IELTS</label> </div>
                <div className='test' id='geietls'><label><input onChange={handleChange} type="radio" style={{visibility:'collapse'}} value="IELTSGeneral" name="test"/>General IELTS</label></div>
                <div className='test' id='toelf'><label><input onChange={handleChange} type="radio" style={{visibility:'collapse'}} value="TOELF" name="test"/>TOELF</label></div>
                <button class="button" onClick={()=>{Version?history.push(Version):alert('Please choose a version')}}><label>Start</label></button>
            </div>
            <div>
                {test==="IELTSAcademic"?<IELTSAcademicVersions/>
                    :test==="IELTSGeneral"?<IELTSGeneralVersions/>
                    :test==="TOELF"?<TOELFVersions/>:null}
            </div>

        </div>
    
    )
}

export default ChooseTest;
