import styled from "styled-components";
import background from "./assets/images/toelf-ielts3.jpg"

export const Wrapper = styled.div`
    width:100vw;
    height:100vh;
    background-image: url(${background});
    background-size:25%;
    opacity: 0.5;
    `

export const EmailStyle = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size:small;
    z-index: 1;
    `
