import React, {useState} from 'react';
import {useHistory} from 'react-router-dom'
import "../Choose.css"

const ChooseTest = () => {
    const history = useHistory()
    const [test, setTest] = useState('');

    const handleChange = ()=>{
        setTest(event.target.value);
    }

    return(
        <div>
            <div className='background'></div>
            <div className="testContainer">
                <div className='test' id='listening' onClick={handleChange('/Listening')}></div>
                <div className='test' id='reading' onClick={handleChange('/Reading')}></div>
                <div className='test' id='writing' onClick={handleChange('/Writing')}></div>
            </div>
            <button class="button" onClick={()=>{(test != '')?history.push(test):alert('Please choose a version')}}><label>Start</label></button>
        </div>
    )
}

export default ChooseTest;
