import React, {useState} from 'react';
import {useHistory} from 'react-router-dom'
import "../Choose.css"

const ChooseTest = () => {
    const history = useHistory()
    const [test, setTest] = useState('/Listening');

   
    function handleChange(selected){
        setTest(selected)
        return test
    }

    
    return(
        <div>
            <div className='background'></div>
            <div className="testContainer">
                <div className='test' id='listening' onClick={async ()=>{handleChange('/Listening').then(data => {console.log(data)})}}><label>Listening</label></div>
                <div className='test' id='reading' onClick={async ()=>{handleChange('/Reading').then(data => {console.log(data)})}}><label>Reading</label></div>
                <div className='test' id='writing' onClick={async ()=>{handleChange('/Writing')}}><label>Writing</label></div>
            </div>
            <button class="button" onClick={()=>{(test != '')?history.push(test):alert('Please choose a version')}}><label>Start</label></button>
        </div>
    )
}

export default ChooseTest;
