import React, {useState} from 'react';
import {useHistory} from 'react-router-dom'
import "../Choose.css"

const ChooseTest = () => {
    const history = useHistory()
    const [test, setTest] = useState('');
    const [version, setVersion] = useState('');
    const handleChange = ()=>{
        setTest(event.target.value);
    }

    const IELTSAcademicVersions = () =>{
        return(
        <>
        <label><input onChange={()=>{setVersion(event.target.value); this.target.checked=true}} type="radio" value="./IELTSAcademicA" name="version"/> Academic IELTS A</label>
        <label><input onChange={()=>{setVersion(event.target.value)}} type="radio" value="./IELTSAcademicB" name="version"/> Academic IELTS B</label>
        </>)    
    }
    const IELTSGeneralVersions = () =>{
        return (
        <>
        <label><input onChange={()=>{setVersion(event.target.value)}} type="radio" value="./IELTSGeneralA" name="version"/> General IELTS A</label>
        <label><input onChange={()=>{setVersion(event.target.value)}} type="radio" value="./IELTSGeneralB" name="version"/> General IELTS B</label>
        </>)
    }
    const TOELFVersions = () =>{
        return (<>
        <label><input onChange={()=>{setVersion(event.target.value)}} type="radio" value="./TOELFA" name="version"/> TOELF A</label>
        <label><input onChange={()=>{setVersion(event.target.value)}} type="radio" value="./TOELFB" name="version"/> TOELF B</label>
        </>)
    }
    return(
        <>
        <h3>Which test are you gonna take?</h3>
        <div>
            <label><input onChange={handleChange} type="radio" value="IELTSAcademic" name="test"/> Academic IELTS </label>
            <label><input onChange={handleChange} type="radio" value="IELTSGeneral" name="test"/> General IELTS </label>
            <label><input onChange={handleChange} type="radio" value="TOELF" name="test"/> TOELF </label>
            {test==="IELTSAcademic"?<IELTSAcademicVersions/>
            :test==="IELTSGeneral"?<IELTSGeneralVersions/>
            :test==="TOELF"?<TOELFVersions/>:null}
        </div>

        <button onClick={()=>{version?history.push(version):alert('Please choose a version')}}>Start</button>
        </>
    
    )
}

export default ChooseTest;
