import React, {useState} from 'react';
import {useHistory} from 'react-router-dom'
import "../Choose.css"

const ChooseTest = () => {
    const history = useHistory()
    const [test, setTest] = useState('');

    const handleChange = ()=>{
        setTest(event.target.value);
    }

    return(
        <div>
            <div className='background'></div>
            <div className="testContainer">
                <div className='test' id='listening'><label><input onChange={handleChange} type="radio" value="/Listening" name="test"/>Listening</label> </div>
                <div className='test' id='reading'><label><input onChange={handleChange} type="radio" style={{visibility:'collapse'}} value="/Reading" name="test"/>Reading</label></div>
                <div className='test' id='writing'><label><input onChange={handleChange} type="radio" style={{visibility:'collapse'}} value="/Writing" name="test"/>Writing</label></div>
            </div>
            <button class="button" onClick={()=>{(test != '')?history.push(test):alert('Please choose a version')}}><label>Start</label></button>
        </div>
    )
}

export default ChooseTest;
