import React, {useState} from 'react';
import {useHistory} from 'react-router-dom'
import "../Choose.css"

const ChooseTest = () => {
    const history = useHistory()
    const [test, setTest] = useState('/Listening');

   
    const handleChange = selected => {
        if (selected == '/Listening'){console.log('this was selected')}

    }

    
    return(
        <div>
            <div className='background'></div>
            <div className="testContainer">
                <div className='test' id='listening' onClick={handleChange('/Listening')}><label>Listening</label></div>
                <div className='test' id='reading' onClick={()=>{setTest('/Reading')}}><label>Reading</label></div>
                <div className='test' id='writing' onClick={()=>{setTest('/Writing')}}><label>Writing</label></div>
            </div>
            <button class="button" onClick={()=>{(test != '')?history.push(test):alert('Please choose a version')}}><label>Start</label></button>
        </div>
    )
}

export default ChooseTest;
