import React, {useState} from 'react';
import {useHistory} from 'react-router-dom'
import "../Choose.css"

const ChooseTest = () => {
    const history = useHistory()
    const [test, setTest] = useState('');

    var Version = '';

    const handleChange = (bid)=>{
        Version = ''
        setTest(event.target.value);
        for(let i=1; i<=3; i++){console.log(i.toString());if(i.toString() != bid){document.getElementById(i.toString()).style.backgroundColor = ''} 
        else{console.log('bid',bid);document.getElementById(bid).style.backgroundColor = 'black'}};
        

    }
    const ChangeColor = (black, normal) =>{
        document.getElementById(black).style.backgroundColor ='black';
        document.getElementById(normal).style.backgroundColor ='';
    }

    const IELTSAcademicVersions = () =>{
        return(
        <>
        <div className='version' id='IAA'><label><input onChange={()=>{Version = "./IELTSAcademicA"; ChangeColor('IAA','IAB')}} type="radio" name="version" /> Academic IELTS A</label></div>
        <div className='version' id='IAB'><label><input onChange={()=>{Version = "./IELTSAcademicB"; ChangeColor('IAB','IAA')}} type="radio" name="version" /> Academic IELTS B</label></div>
        </>)    
    }
    const IELTSGeneralVersions = () =>{
        return (
        <>
        <div className='version' id='IGA'><label><input onChange={()=>{Version = "./IELTSGeneralA"; ChangeColor('IGA','IAB')}} type="radio" name="version"/> General IELTS A</label></div>
        <div className='version' id='IGB'><label><input onChange={()=>{Version = "./IELTSGeneralB"; ChangeColor('IGB','IGA')}} type="radio" name="version"/> General IELTS B</label></div>
        </>)
    }
    const TOELFVersions = () =>{
        return (<>
        <div className='version' id='TA'><label><input onChange={()=>{Version = "./TOELFA"; ChangeColor('TA','TB')}} type="radio" name="version"/> TOELF A</label></div>
        <div className='version' id='TB'><label><input onChange={()=>{Version = "./TOELFB"; ChangeColor('TB','TA')}} type="radio" name="version"/> TOELF B</label></div>
        </>)
    }
    return(
        <>
        <div className="testContainer">
        <h3>SELECT THE TEST</h3>
            <div className='test' id='1'><label><input onChange={handleChange('1')} type="radio" value="IELTSAcademic" name="test"/>Academic IELTS</label> </div>
            <div className='test' id='2'><label><input onChange={handleChange('2')} type="radio" value="IELTSGeneral" name="test"/>General IELTS</label></div>
            <div className='test' id='3'><label><input onChange={handleChange('3')} type="radio" value="TOELF" name="test"/>TOELF</label></div>
            {test==="IELTSAcademic"?<IELTSAcademicVersions/>
            :test==="IELTSGeneral"?<IELTSGeneralVersions/>
            :test==="TOELF"?<TOELFVersions/>:null}
        </div>

        <button class="button" onClick={()=>{Version?history.push(Version):alert('Please choose a version')}}>Start</button>
        </>
    
    )
}

export default ChooseTest;
