import React from 'react'
import {useHistory} from 'react-router-dom'
import {useForm} from 'react-hook-form'
import validator from 'validator'
import {EmailStyle, Wrapper} from '../../styles'
import background from "../../assets/images/Green.jpg"

const EmailForm = (props)=>{
    const history = useHistory();
    const {register, handleSubmit, formState: {errors}} = useForm();
    const onSubmit = data => {
        if(validator.isEmail(data.email)){alert("Nice Email"); history.push(props.path)}
        else{alert("Please provide a valid E-mail")}
        
    }
    return(
        <Wrapper>
            <EmailStyle style={{}}>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <input type="text" style={{fontSize:"250%"}} placeholder='E-mail' {...register("email")} />
                    <input type="submit" style={{fontSize:"250%", backgroundColor:'white', border:'solid black 10px', marginLeft:'5px', backgroundImage: `url(${background})`}} value={"Confirm"}/>
                </form>
            </EmailStyle>
        </Wrapper>

    )

}

export default EmailForm
