import React, {useEffect, useState} from 'react';
import {useHistory} from 'react-router-dom'
import "../Choose.css"

const ChooseTest = () => {
    const history = useHistory()
    const [test, setTest] = useState('');

   
    function handleChange(selected){
        setTest(selected)
    }

    useEffect(()=>{
        document.getElementById(test).style.border='solid red'
    })

    return(
        <div>
            <div className='background'></div>
            <div className="testContainer">
                <div className='test' id='listening' onClick={()=>{handleChange('listening')}}><label>Listening</label></div>
                <div className='test' id='reading' onClick={()=>{handleChange('reading')}}><label>Reading</label></div>
                <div className='test' id='writing' onClick={()=>{handleChange('writing')}}><label>Writing</label></div>
            </div>
            <button class="button" onClick={()=>{(test != '')?history.push('/'+test):alert('Please choose a version')}}><label>Start</label></button>
        </div>
    )
}

export default ChooseTest;
