import React, {useState} from 'react';
import {useHistory} from 'react-router-dom'
import "../Choose.css"

const ChooseTest = () => {
    const history = useHistory()
    const [test, setTest] = useState('');
    const [version, setVersion] = useState('');
    const handleChange = ()=>{
        setVersion(false)
        setTest(event.target.value);
    }
    var Version = '';
    const ChangeColor = (black, normal) =>{
        document.getElementById(black).style.backgroundColor ='black';
        document.getElementById(normal).style.backgroundColor ='';
    }


    const IELTSAcademicVersions = () =>{
        return(
        <>
        <button className='version' id='IAA'><label><input onChange={()=>{Version = "./IELTSAcademicA"; ChangeColor('IAA','IAB')}} type="radio" name="version" /> Academic IELTS A</label></button>
        <button className='version' id='IAB'><label><input onChange={()=>{Version = "./IELTSAcademicB"; ChangeColor('IAB','IAA')}} type="radio" name="version" /> Academic IELTS B</label></button>
        </>)    
    }
    const IELTSGeneralVersions = () =>{
        return (
        <>
        <div className='version'><label><input onChange={()=>{setVersion(event.target.value)}} type="radio" value="./IELTSGeneralA" name="version"/> General IELTS A</label></div>
        <div className='version'><label><input onChange={()=>{setVersion(event.target.value)}} type="radio" value="./IELTSGeneralB" name="version"/> General IELTS B</label></div>
        </>)
    }
    const TOELFVersions = () =>{
        return (<>
        <div className='version'><label><input onChange={()=>{setVersion(event.target.value)}} type="radio" value="./TOELFA" name="version"/> TOELF A</label></div>
        <div className='version'><label><input onChange={()=>{setVersion(event.target.value)}} type="radio" value="./TOELFB" name="version"/> TOELF B</label></div>
        </>)
    }
    return(
        <>
        <div className="testContainer">
        <h3>SELECT THE TEST</h3>
            <div className='test'><label><input onChange={handleChange} type="radio" value="IELTSAcademic" name="test"/>Academic IELTS</label> </div>
            <div className='test'><label><input onChange={handleChange} type="radio" value="IELTSGeneral" name="test"/>General IELTS</label></div>
            <div className='test'><label><input onChange={handleChange} type="radio" value="TOELF" name="test"/>TOELF</label></div>
            {test==="IELTSAcademic"?<IELTSAcademicVersions/>
            :test==="IELTSGeneral"?<IELTSGeneralVersions/>
            :test==="TOELF"?<TOELFVersions/>:null}
        </div>

        <button class="button" onClick={()=>{Version?history.push(Version):alert('Please choose a version')}}>Start</button>
        </>
    
    )
}

export default ChooseTest;
